import React from "react";
import {connect} from "react-redux";
import { Route, Switch, BrowserRouter as Router, Link, withRouter } from "react-router-dom";

import axios from 'axios';

import { Layout, Drawer, Navigation, Content } from "react-mdl";
import { Container, Row, Col, } from "react-bootstrap";
import { CommentOutlined, UserOutlined } from "@ant-design/icons";

import { SERVER_URL } from '../config';

import Signup from './signup';
import Chat from './chat';

const Dashboard = ({ user, history }) => {
  const [users, setUsers] = React.useState([]);
  React.useEffect(() => {
    // if (user.role === 1) {
      axios
        .get(SERVER_URL + "users/allusers")
        .then(res => res.data)
        .then(datas => {
          console.log(datas);
          setUsers(datas);
        });
    // }
  }, [history]);
  return (
    <div style={{ height: "calc(100vh - 60px)", position: "relative" }}>
      <Router>
        <Layout fixedDrawer>
          <Drawer title="Menu">
            <Navigation>
              <Link to="/dashboard/user">
                <UserOutlined />
                User
              </Link>
              <Link to="/dashboard/chat">
                <CommentOutlined />
                Chat
              </Link>
            </Navigation>
          </Drawer>
          <Content>
            <Switch>
              <Route path="/dashboard/user">
                <Container>
                  <Row>
                    <Signup />
                  </Row>
                </Container>
              </Route>
              <Route path="/dashboard/chat"><Chat users={users} manager={user} /></Route>
            </Switch>
          </Content>
        </Layout>
      </Router>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    user: state.user
  }
}

export default connect(mapStateToProps)(withRouter(Dashboard));
