import React from 'react';
import {  Button } from 'antd';
import { SERVER_URL } from '../../config';
import {connect} from "react-redux";
import{List,ListItem,ListItemContent,ProgressBar}from 'react-mdl'



  class Users extends React.Component {

    constructor(props){
      super(props);
      this.state = {
          isLoading: false,
          users: []
      }
  }



componentDidMount = () => {
  fetch(SERVER_URL + "users/allusers")
  .then(res=>res.json())
  .then(json=>{
    this.setState({
      isLoading:true,
      users:json,
    })
  })

  }


    render(){
      const{isLoading,users}=this.state;
      if(!isLoading){
        return <ProgressBar indeterminate />
      }else {return (
        <div style={{height: '300px' }}>


          <Button type="primary" ><a href="/dashboard/user/add">Add new user</a></Button>





          <List style={{width: '650px'}}>
          {users.map(user=>
    <ListItem threeLine >
    <ListItemContent avatar={user.image}  subtitle={user.email}>{user.name}</ListItemContent>
    </ListItem>)}
</List>
</div>
      );
    }}}
    const mapStateToProps = (state) => {
      return {
        user: state.user
      }
    }

    export default connect(mapStateToProps)(Users) ;
